"use client"

import type React from "react"
import Banner from "@components/Banner"

import { useEffect, useState } from "react"

export default function Background({ children }: { children?: React.ReactNode }) {
  const [blobs, setBlobs] = useState<Array<{ x: number; y: number; size: number; rotation: number }>>([])

  useEffect(() => {
    // Generate random pink gradient blobs
    const numberOfBlobs = 6
    const newBlobs = []

    for (let i = 0; i < numberOfBlobs; i++) {
      newBlobs.push({
        x: Math.random() * 100,
        y: Math.random() * 100,
        size: Math.random() * 300 + 150, // 150-450px
        rotation: Math.random() * 360,
      })
    }

    setBlobs(newBlobs)
  }, [])

  return (
    <div className="relative min-h-screen w-full overflow-hidden bg-neutral-100">
      {/* Grid overlay */}
      <div
        className="absolute inset-0 z-0 opacity-20"
        style={{
          backgroundImage:
            "linear-gradient(to right, #d1d1d1 1px, transparent 1px), linear-gradient(to bottom, #d1d1d1 1px, transparent 1px)",
          backgroundSize: "20px 20px",
        }}
      />

      {/* Pink gradient blobs */}
      {blobs.map((blob, index) => (
        <div
          key={index}
          className="absolute rounded-full opacity-30"
          style={{
            left: `${blob.x}%`,
            top: `${blob.y}%`,
            width: `${blob.size}px`,
            height: `${blob.size}px`,
            background: "linear-gradient(45deg, #ffb6c1, #ff69b4, #ff1493, #db7093)",
            filter: "blur(60px)",
            transform: `rotate(${blob.rotation}deg)`,
          }}
        />
      ))}

      {/* Enhanced grain texture overlay */}
      <div
        className="absolute inset-0 z-10 mix-blend-overlay opacity-40"
        style={{
          backgroundImage: `url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E")`,
          backgroundSize: "200px 200px",
        }}
      />

      {/* Additional grain layer for more texture */}
      <div
        className="absolute inset-0 z-20 mix-blend-multiply opacity-10"
        style={{
          backgroundImage: `url("data:image/png;base64,")`,
          backgroundSize: "30px 30px",
        }}
      />

      {/* Content */}
      <div className="relative z-30">{children}</div>

      <Banner />

    </div>
  )
}
